import React, { useRef, useEffect, useState } from 'react';
import './Video.css';

function Video({ src, channel, description, likes, comments, shares, isAd, link }) {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(true);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          videoRef.current.play().catch(() => {});
        } else {
          videoRef.current.pause();
        }
      },
      { threshold: 0.8 }
    );

    observer.observe(videoRef.current);
    return () => observer.disconnect();
  }, []);

  const toggleMute = () => {
    setIsMuted(!isMuted);
    if (videoRef.current) {
      videoRef.current.muted = !videoRef.current.muted;
    }
  };

  return (
    <div className="video">
      <video
        ref={videoRef}
        className="video__player"
        src={src}
        loop
        muted={isMuted}
      ></video>

      <div className="video__info">
        <div className="video__sidebar">
          <div className="video__stat">‚ù§Ô∏è {likes || 0}</div>
          <div className="video__stat">üí¨ {comments || 0}</div>
          <div className="video__stat">üîó {shares || 0}</div>
        </div>

        <div className="video__footer">
          <p className="video__channel">{channel} <button>Abonnieren</button></p>
          <p className="video__description">{description}</p>
          {isAd && link && (
            <a className="video__link" href={link} target="_blank" rel="noopener noreferrer">
              üëâ –ü–µ—Ä–µ–π—Ç–∏ –ø–æ —Å—Å—ã–ª–∫–µ
            </a>
          )}
        </div>
      </div>

      <button className="video__muteBtn" onClick={toggleMute}>
        {isMuted ? 'üîá' : 'üîä'}
      </button>
    </div>
  );
}

export default Video;
