import React, { useState } from "react";
import "./Video.css";

function formatCount(count) {
  if (typeof count === "string") {
    count = parseFloat(count.replace(/[^\d.]/g, ""));
    if (/m/i.test(count)) count *= 1_000_000;
    else if (/k/i.test(count)) count *= 1_000;
  }
  if (count >= 1_000_000) return (count / 1_000_000).toFixed(1) + "M";
  if (count >= 1_000) return (count / 1_000).toFixed(1) + "K";
  return count;
}

function Video({
  url,
  channel,
  description,
  likes = 0,
  dislikes = 0,
  comments = 0,
}) {
  const [likeCount, setLikeCount] = useState(Number(likes) || 0);
  const [dislikeCount, setDislikeCount] = useState(Number(dislikes) || 0);
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);
  const [muted, setMuted] = useState(true);

  const onLike = () => {
    if (isLiked) {
      setLikeCount((prev) => prev - 1);
      setIsLiked(false);
    } else {
      setLikeCount((prev) => prev + 1);
      setIsLiked(true);
      if (isDisliked) {
        setDislikeCount((prev) => prev - 1);
        setIsDisliked(false);
      }
    }
  };

  const onDislike = () => {
    if (isDisliked) {
      setDislikeCount((prev) => prev - 1);
      setIsDisliked(false);
    } else {
      setDislikeCount((prev) => prev + 1);
      setIsDisliked(true);
      if (isLiked) {
        setLikeCount((prev) => prev - 1);
        setIsLiked(false);
      }
    }
  };

  const onComment = () => {
    alert("Kommentare werden bald verf√ºgbar sein.");
  };

  const onShare = () => {
    alert("Link wurde kopiert!");
  };

  const toggleMute = (e) => {
    e.target.muted = !e.target.muted;
    setMuted(e.target.muted);
  };

  return (
    <div className="video">
      <video
        className="video__player"
        src={url}
        autoPlay
        loop
        muted={muted}
        playsInline
        onClick={toggleMute}
      />

      <div className="video__sidebar">
        <div
          className={`video__button ${isLiked ? "active" : ""}`}
          onClick={onLike}
        >
          {/* üëç Like SVG */}
          <svg height="28" viewBox="0 0 24 24" fill="white">
            <path d="M1 21h4V9H1v12zM23 10c0-1.1-.9-2-2-2h-6.31l.95-4.57.03-.32c0-.41-.17-.79-.44-1.06L14.17 1 7.59 7.59C7.21 7.95 7 8.45 7 9v10c0 1.1.9 2 2 2h7c.78 0 1.48-.45 1.82-1.14l3.58-7.16c.08-.16.12-.34.12-.52v-1.18L23 10z" />
          </svg>
          <span>{formatCount(likeCount)}</span>
        </div>

        <div
          className={`video__button ${isDisliked ? "active" : ""}`}
          onClick={onDislike}
        >
          {/* üëé Dislike SVG */}
          <svg height="28" viewBox="0 0 24 24" fill="white">
            <path d="M15 3H8c-.78 0-1.48.45-1.82 1.14L2.6 11.3c-.08.16-.12.34-.12.52v1.18C2.48 13.55 2.9 14 3.45 14H9.8l-.95 4.57c-.05.24.03.5.22.67l.59.59L16.41 16.4c.37-.36.59-.86.59-1.41V5c0-1.1-.9-2-2-2zM22 3h-4v12h4V3z" />
          </svg>
          <span>{formatCount(dislikeCount)}</span>
        </div>

        <div className="video__button" onClick={onComment}>
          {/* üí¨ Comment SVG */}
          <svg height="28" viewBox="0 0 24 24" fill="white">
            <path d="M21 6h-2v9H6l-4 4V6c0-1.1.9-2 2-2h17c1.1 0 2 .9 2 2v12h-2V6z" />
          </svg>
          <span>{formatCount(comments)}</span>
        </div>

        <div className="video__button" onClick={onShare}>
          {/* üîÑ Share SVG */}
          <svg height="28" viewBox="0 0 24 24" fill="white">
            <path d="M18 16.08c-.76 0-1.44.3-1.96.77L8.91 12.7a2.5 2.5 0 0 0 0-1.39l7.05-4.11A2.5 2.5 0 1 0 14.5 5a2.5 2.5 0 0 0 2.45 3.09l-7.05 4.11a2.5 2.5 0 0 0 0 1.39l7.13 4.15a2.5 2.5 0 1 0 .97-1.66z" />
          </svg>
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          <button>Abonnieren</button>
        </div>
        <div className="video__description">{description}</div>
      </div>
    </div>
  );
}

export default Video;
