import React, { useState } from "react";
import CommentsModal from "./CommentsModal";
import "./Video.css";

function Video({ url, channel, description, likes, dislikes, comments }) {
  const [likeCount, setLikeCount] = useState(likes);
  const [dislikeCount, setDislikeCount] = useState(dislikes);
  const [isCommentsOpen, setIsCommentsOpen] = useState(false);

  const exampleComments = [
    { user: "Alice", text: "–ö—Ä—É—Ç–æ–µ –≤–∏–¥–µ–æ!" },
    { user: "Bob", text: "–û—á–µ–Ω—å –º–∏–ª–æ üê±" },
    { user: "–ú–∞—Ä–∏—è", text: "–°–¥–µ–ª–∞–π—Ç–µ –µ—â–µ!" },
  ];

  const handleLike = () => setLikeCount((prev) => prev + 1);
  const handleDislike = () => setDislikeCount((prev) => prev + 1);
  const handleShare = () => alert("–°—Å—ã–ª–∫–∞ —Å–∫–æ–ø–∏—Ä–æ–≤–∞–Ω–∞!");
  const handleComments = () => setIsCommentsOpen(true);

  return (
    <div className="video">
      <video
        className="video__player"
        src={url}
        autoPlay
        loop
        muted
        playsInline
        controls={false}
      />

      <div className="video__buttons">
        <div className="video__button" onClick={handleLike}>
          <img src={process.env.PUBLIC_URL + "/icons/like.svg"} alt="Like" />
          <span>{likeCount}</span>
        </div>
        <div className="video__button" onClick={handleDislike}>
          <img
            src={process.env.PUBLIC_URL + "/icons/dislike.svg"}
            alt="Dislike"
          />
          <span>{dislikeCount}</span>
        </div>
        <div className="video__button" onClick={handleComments}>
          <img
            src={process.env.PUBLIC_URL + "/icons/comment.svg"}
            alt="Comments"
          />
          <span>{comments}</span>
        </div>
        <div className="video__button" onClick={handleShare}>
          <img
            src={process.env.PUBLIC_URL + "/icons/share.svg"}
            alt="Share"
          />
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          <button>–ü–æ–¥–ø–∏—Å–∞—Ç—å—Å—è</button>
        </div>
        <div className="video__description">{description}</div>
      </div>

      <CommentsModal
        isOpen={isCommentsOpen}
        onClose={() => setIsCommentsOpen(false)}
        comments={exampleComments}
      />
    </div>
  );
}

export default Video;
