import React, { useEffect, useRef, useState } from "react";
import "./Video.css";

export default function Video({
  url,
  channel,
  description,
  likes,
  dislikes,
  comments,
  isAd = false,
  link,
  isActive,
  onActivate
}) {
  const ref = useRef();
  const [muted, setMuted] = useState(false); // звук включён по умолчанию
  const [userInteracted, setUserInteracted] = useState(false);
  const [loading, setLoading] = useState(true);

  // фикс для autoplay со звуком — ждём первого клика
  useEffect(() => {
    const handler = () => {
      setUserInteracted(true);
      window.removeEventListener("click", handler);
    };
    window.addEventListener("click", handler);
    return () => window.removeEventListener("click", handler);
  }, []);

  // Автозапуск и пауза через Intersection Observer
  useEffect(() => {
    const video = ref.current;
    if (!video) return;
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting && isActive && userInteracted) {
          video.muted = muted;
          video.play().catch(() => {});
        } else {
          video.pause();
        }
      },
      { threshold: 0.6 }
    );
    observer.observe(video);
    return () => observer.disconnect();
  }, [isActive, muted, userInteracted]);

  return (
    <div className="video" onClick={onActivate}>
      <video
        ref={ref}
        className="video__player"
        src={url}
        loop
        autoPlay
        playsInline
        onCanPlay={() => setLoading(false)}
      />

      {loading && <div className="video__loading">Загрузка…</div>}

      <button
        className="video__muteBtn"
        onClick={e => {
          e.stopPropagation();
          const newMuted = !muted;
          setMuted(newMuted);
          if (ref.current) {
            ref.current.muted = newMuted;
          }
        }}
      >
        <img
          src={process.env.PUBLIC_URL + (muted ? "/icons/mute.svg" : "/icons/sound.svg")}
          alt={muted ? "Muted" : "Sound on"}
          width={20}
          height={20}
        />
      </button>

      <div className="video__sidebar">
        <div className="video__button">
          <img src="/icons/like.svg" alt="Like" width={30} />
          <span>{likes}</span>
        </div>
        <div className="video__button">
          <img src="/icons/dislike.svg" alt="Dislike" width={30} />
          <span>{dislikes}</span>
        </div>
        <div className="video__button">
          <img src="/icons/comment.svg" alt="Comments" width={30} />
          <span>{comments}</span>
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <strong>@{channel}</strong>
          <button>Подписаться</button>
        </div>
        <div className="video__description">{description}</div>
        {isAd && link && (
          <a href={link} className="video__adLink" target="_blank" rel="noreferrer">
            Перейти к рекламодателю →
          </a>
        )}
      </div>
    </div>
  );
}
