import React, { useState } from "react";
import "./Video.css";

function formatCount(count) {
  if (typeof count === "string") {
    count = parseFloat(count.replace(/[^\d.]/g, ""));
    if (/m/i.test(count)) count *= 1_000_000;
    else if (/k/i.test(count)) count *= 1_000;
  }
  if (count >= 1_000_000) return (count / 1_000_000).toFixed(1) + "M";
  if (count >= 1_000) return (count / 1_000).toFixed(1) + "K";
  return count;
}

function Video({
  url,
  channel,
  description,
  likes = 0,
  dislikes = 0,
  comments = 0,
  isAd = false,
}) {
  const [likeCount, setLikeCount] = useState(Number(likes));
  const [dislikeCount, setDislikeCount] = useState(Number(dislikes));
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);
  const [muted, setMuted] = useState(true);
  const [loading, setLoading] = useState(true);

  const onLike = () => {
    if (isLiked) {
      setLikeCount((prev) => prev - 1);
      setIsLiked(false);
    } else {
      setLikeCount((prev) => prev + 1);
      setIsLiked(true);
      if (isDisliked) {
        setDislikeCount((prev) => prev - 1);
        setIsDisliked(false);
      }
    }
  };

  const onDislike = () => {
    if (isDisliked) {
      setDislikeCount((prev) => prev - 1);
      setIsDisliked(false);
    } else {
      setDislikeCount((prev) => prev + 1);
      setIsDisliked(true);
      if (isLiked) {
        setLikeCount((prev) => prev - 1);
        setIsLiked(false);
      }
    }
  };

  const onComment = () => {
    alert("–ö–æ–º–º–µ–Ω—Ç–∞—Ä–∏–∏ —Å–∫–æ—Ä–æ –ø–æ—è–≤—è—Ç—Å—è.");
  };

  const onShare = () => {
    alert("–°—Å—ã–ª–∫–∞ —Å–∫–æ–ø–∏—Ä–æ–≤–∞–Ω–∞!");
  };

  return (
    <div className="video">
      <video
        className="video__player"
        src={url}
        autoPlay
        loop
        muted={muted}
        playsInline
        onClick={() => setMuted(!muted)}
        onCanPlay={() => setLoading(false)}
      />

      {loading && <div className="video__loading">–ó–∞–≥—Ä—É–∑–∫–∞...</div>}

      <div className="video__mute" onClick={() => setMuted(!muted)}>
        {muted ? "üîá" : "üîä"}
      </div>

      <div className="video__sidebar">
        <div className={`video__button ${isLiked ? "active" : ""}`} onClick={onLike}>
          <svg height="28" viewBox="0 0 24 24" fill="white">
            <path d="M1 21h4V9H1v12zM23 10...z" />
          </svg>
          <span>{formatCount(likeCount)}</span>
        </div>

        <div className={`video__button ${isDisliked ? "active" : ""}`} onClick={onDislike}>
          <svg height="28" viewBox="0 0 24 24" fill="white">
            <path d="M15 3H8c-.78...z" />
          </svg>
          <span>{formatCount(dislikeCount)}</span>
        </div>

        <div className="video__button" onClick={onComment}>
          üí¨<span>{formatCount(comments)}</span>
        </div>

        <div className="video__button" onClick={onShare}>üîó</div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          {!isAd && <button>Abonnieren</button>}
        </div>
        <div className="video__description">{description}</div>
        {isAd && (
          <button
            className="register-btn"
            onClick={() => window.location.href = "/signup"}
          >
            –ó–∞—Ä–µ–≥–∏—Å—Ç—Ä–∏—Ä–æ–≤–∞—Ç—å—Å—è
          </button>
        )}
      </div>
    </div>
  );
}

export default Video;
