import React, { useEffect, useRef, useState } from "react";
import "./Video.css";

export default function Video({
  url,
  channel,
  description,
  likes,
  dislikes,
  comments,
  isAd = false,
  link,
  isActive
}) {
  const ref = useRef();
  const [muted, setMuted] = useState(false);
  const [loading, setLoading] = useState(true);
  const [statusMessage, setStatusMessage] = useState("");

  useEffect(() => {
    const video = ref.current;
    if (!video) return;

    video.muted = muted;

    if (isActive) {
      video.play().catch((e) => {
        console.warn("Autoplay error:", e.message);
      });
    } else {
      video.pause();
    }
  }, [isActive, muted]);

  const handleToggleMute = (e) => {
    const ignoreTags = ["BUTTON", "A", "IMG", "svg", "path"];
    if (ignoreTags.includes(e.target.tagName)) return;

    const newMuted = !muted;
    setMuted(newMuted);
    if (ref.current) {
      ref.current.muted = newMuted;
    }
    setStatusMessage(newMuted ? "Видео в беззвучном режиме" : "Звук включён");
    setTimeout(() => setStatusMessage(""), 2000);
  };

  return (
    <div className="video" onClick={handleToggleMute}>
      <video
        ref={ref}
        className="video__player"
        src={url}
        loop
        autoPlay
        playsInline
        muted={muted}
        onCanPlay={() => setLoading(false)}
      />

      {loading && <div className="video__loading">Загрузка…</div>}

      {statusMessage && (
        <div className="video__mutedMessage">{statusMessage}</div>
      )}

      {/* Sidebar */}
      <div className="video__sidebar">
        <div className="video__button">
          <img src="/icons/like.svg" alt="Like" width={30} />
          <span>{likes}</span>
        </div>
        <div className="video__button">
          <img src="/icons/dislike.svg" alt="Dislike" width={30} />
          <span>{dislikes}</span>
        </div>
        <div className="video__button">
          <img src="/icons/comment.svg" alt="Comment" width={30} />
          <span>{comments}</span>
        </div>
      </div>

      {/* Footer */}
      <div className="video__footer">
        <div className="video__channel">
          <strong>@{channel}</strong>
          <button>Подписаться</button>
        </div>
        <div className="video__description">{description}</div>
        {isAd && link && (
          <a
            href={link}
            className="video__adLink"
            target="_blank"
            rel="noreferrer"
          >
            Перейти к рекламодателю →
          </a>
        )}
      </div>
    </div>
  );
}
