import React, { useState, useEffect, Suspense, useMemo } from 'react';
import './App.css';
import Video from './components/Video';
import BottomNav from './components/BottomNav';
import videos from './config.json';

function App() {
  const [activeId, setActiveId] = useState(0);
  const [visibleVideos, setVisibleVideos] = useState(() => videos.slice(0, 3));

  const loadMoreVideos = () => {
    const nextVideos = videos.slice(visibleVideos.length, visibleVideos.length + 3);
    setVisibleVideos(prev => [...prev, ...nextVideos]);
  };

  useEffect(() => {
    const handleScroll = () => {
      if (window.innerHeight + window.scrollY >= document.body.offsetHeight - 500) {
        loadMoreVideos();
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, [visibleVideos]);

  const videoList = useMemo(() => {
    return visibleVideos.map((video, index) => (
      <Video
        key={index}
        {...video}
        isActive={activeId === index}
        onActivate={() => setActiveId(index)}
      />
    ));
  }, [visibleVideos, activeId]);

  return (
    <div className="app">
      <Suspense fallback={<div className="loading">Загрузка царских видео... ✨</div>}>
        {videoList}
      </Suspense>
      <BottomNav />
    </div>
  );
}

export default App;
