import React, { useState } from "react";
import Video from "./components/Video";
import BottomNav from "./components/BottomNav";
import "./App.css";

function App() {
  const [activeId, setActiveId] = useState(1);

  const videos = [
    {
      id: 1,
      url: process.env.PUBLIC_URL + "/videos/Book_of_dead.mp4",
      poster: process.env.PUBLIC_URL + "/posters/Book_of_dead.jpg",
      channel: "@adSponsor",
      description: "üé∞ Book of Dead",
      likes: "1.5M",
      comments: "6.1K",
      isAd: true,
      link: "https://example.com/book-of-dead"
    },
    {
      id: 2,
      url: process.env.PUBLIC_URL + "/videos/first.mp4",
      poster: process.env.PUBLIC_URL + "/posters/first.jpg",
      channel: "–ö–∞–Ω–∞–ª Eins",
      description: "Erstes Video ‚Äì kurze Beschreibung",
      likes: "2.1M",
      comments: "13.5K"
    },
    {
      id: 3,
      url: process.env.PUBLIC_URL + "/videos/second.mp4",
      poster: process.env.PUBLIC_URL + "/posters/second.jpg",
      channel: "@boatsdotcom",
      description: "Check out the secret cabin...",
      likes: "2M",
      comments: "7.5K"
    }
    // ...–¥–æ–±–∞–≤—å –æ—Å—Ç–∞–ª—å–Ω—ã–µ –≤–∏–¥–µ–æ –ø—Ä–∏ –Ω–µ–æ–±—Ö–æ–¥–∏–º–æ—Å—Ç–∏
  ];

  return (
    <div className="app">
      {videos.map((video) => (
        <Video
          key={video.id}
          {...video}
          isActive={activeId === video.id}
          onActivate={() => setActiveId(video.id)}
        />
      ))}
      <BottomNav />
    </div>
  );
}

export default App;
