import React from "react";
import "./Video.css";

function Video({ url, channel, description, likes, dislikes, comments }) {
  const handleLike = () => {
    console.log("Liked!");
  };

  const handleDislike = () => {
    console.log("Disliked!");
  };

  const handleComment = () => {
    console.log("Comment clicked");
  };

  const handleShare = () => {
    console.log("Share clicked");
  };

  return (
    <div className="video">
      <video
        className="video__player"
        src={url}
        controls
        loop
        muted
        playsInline
      ></video>

      <div className="video__sidebar">
        <div className="video__button" onClick={handleLike}>
          <img
            src={process.env.PUBLIC_URL + "/icons/like.svg"}
            alt="Like"
          />
          <span>{likes}</span>
        </div>
        <div className="video__button" onClick={handleDislike}>
          <img
            src={process.env.PUBLIC_URL + "/icons/dislike.svg"}
            alt="Dislike"
          />
          <span>{dislikes}</span>
        </div>
        <div className="video__button" onClick={handleComment}>
          <img
            src={process.env.PUBLIC_URL + "/icons/comment.svg"}
            alt="Comment"
          />
          <span>{comments}</span>
        </div>
        <div className="video__button" onClick={handleShare}>
          <img
            src={process.env.PUBLIC_URL + "/icons/share.svg"}
            alt="Share"
          />
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          <button>Подписаться</button>
        </div>
        <div className="video__description">{description}</div>
      </div>
    </div>
  );
}

export default Video;
