import React, { useRef, useEffect, useState } from "react";
import "./Video.css";

function formatCount(countStr) {
  let n = typeof countStr === "string"
    ? parseFloat(countStr.replace(/[^\d.]/g, "")) || 0
    : Number(countStr) || 0;
  if (n >= 1_000_000) return (n / 1_000_000).toFixed(1) + "M";
  if (n >= 1_000) return (n / 1_000).toFixed(1) + "K";
  return n.toString();
}

export default function Video({
  url,
  channel,
  description,
  likes,
  dislikes,
  comments,
  isAd = false,
  link,
  isActive,
  onActivate
}) {
  const ref = useRef();
  const [muted, setMuted] = useState(true);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const v = ref.current;
    if (!v) return;
    const obs = new IntersectionObserver(
      ([e]) => {
        if (e.isIntersecting && isActive) {
          v.play().catch(() => {});
        } else {
          v.pause();
        }
      },
      { threshold: 0.6 }
    );
    obs.observe(v);
    return () => obs.disconnect();
  }, [isActive]);

  return (
    <div className="video" onClick={onActivate}>
      <video
        ref={ref}
        className="video__player"
        src={url}
        loop
        muted={muted}
        playsInline
        onCanPlay={() => setLoading(false)}
      />
      {loading && <div className="video__loading">Wird geladen…</div>}

      <button
        className="video__muteBtn"
        onClick={e => {
          e.stopPropagation();
          setMuted(!muted);
        }}
      >
        {muted ? "🔇" : "🔊"}
      </button>

      <div className="video__sidebar">
        <div className="video__button" onClick={e => e.stopPropagation()}>
          {/* ❤️ Лайк */}
          <svg xmlns="http://www.w3.org/2000/svg" className="video__icon" viewBox="0 0 24 24" width="32" height="32" fill="currentColor">
            <path d="M12 21.35l-1.45-1.32C5.4 15.36 2 12.28 2 8.5 2 6.5 3.5 5 5.5 5c1.54 0 3.04.99 3.57 2.36h1.87C13.46 5.99 14.96 5 16.5 5 18.5 5 20 6.5 20 8.5c0 3.78-3.4 6.86-8.55 11.54L12 21.35z"/>
          </svg>
          <span>{formatCount(likes)}</span>
        </div>

        <div className="video__button" onClick={e => e.stopPropagation()}>
          {/* 👎 Дизлайк */}
          <svg xmlns="http://www.w3.org/2000/svg" className="video__icon" viewBox="0 0 24 24" width="32" height="32" fill="currentColor">
            <path d="M12 2.65l1.45 1.32C18.6 8.64 22 11.72 22 15.5c0 2-1.5 3.5-3.5 3.5-1.54 0-3.04-.99-3.57-2.36h-1.87C10.54 20.01 9.04 21 7.5 21 5.5 21 4 19.5 4 17.5c0-3.78 3.4-6.86 8.55-11.54L12 2.65z"/>
          </svg>
          <span>{formatCount(dislikes)}</span>
        </div>

        <div className="video__button" onClick={e => e.stopPropagation()}>
          {/* 💬 Комментарий */}
          <svg xmlns="http://www.w3.org/2000/svg" className="video__icon" viewBox="0 0 24 24" width="32" height="32" fill="currentColor">
            <path d="M21 6h-2v9H6l-4 4V6c0-1.1.9-2 2-2h17c1.1 0 2 .9 2 2v12h-2V6z"/>
          </svg>
          <span>{formatCount(comments)}</span>
        </div>

        <div className="video__button" onClick={e => e.stopPropagation()}>
          {/* 🔗 Поделиться */}
          <svg xmlns="http://www.w3.org/2000/svg" className="video__icon" viewBox="0 0 24 24" width="32" height="32" fill="currentColor">
            <path d="M18 16.08c-.76 0-1.44.3-1.96.77L8.91 12.7a2.5 2.5 0 0 0 0-1.39l7.05-4.11A2.5 2.5 0 1 0 14.5 5a2.5 2.5 0 0 0 2.45 3.09l-7.05 4.11a2.5 2.5 0 0 0 0 1.39l7.13 4.15a2.5 2.5 0 1 0 .97-1.66z"/>
          </svg>
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          <button>Abonnieren</button>
        </div>
        <div className="video__description">{description}</div>
        {isAd && link && (
          <a
            className="video__adLink"
            href={link}
            target="_blank"
            rel="noopener noreferrer"
          >
            👉 Hier klicken
          </a>
        )}
      </div>
    </div>
  );
}
