import React, { useRef, useEffect, useState } from 'react';
import '../components/Video.css';

const Video = ({ src, title, adLink, likes = 0 }) => {
  const videoRef = useRef(null);
  const [muted, setMuted] = useState(true);
  const [showMutedNotice, setShowMutedNotice] = useState(false);

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.play().catch((err) => console.error('Autoplay failed:', err));
    }
  }, [src]);

  useEffect(() => {
    if (videoRef.current) {
      videoRef.current.muted = muted;
    }
  }, [muted]);

  const handleToggleMute = () => {
    setMuted((prev) => !prev);
    setShowMutedNotice(true);
    setTimeout(() => setShowMutedNotice(false), 2000);
  };

  return (
    <div className="video" onClick={handleToggleMute}>
      <video
        className="video__player"
        ref={videoRef}
        src={src}
        autoPlay
        loop
        playsInline
        muted={muted}
      />
      
      <div className="video__sidebar">
        <div className="video__button">
          <img src="/icons/like.svg" alt="Like" />
          <span>{likes}</span>
        </div>
        <div className="video__button">
          <img src="/icons/comment.svg" alt="Comment" />
        </div>
        <div className="video__button">
          <img src="/icons/share.svg" alt="Share" />
        </div>
      </div>

      {adLink && (
        <a className="video__adLink" href={adLink} target="_blank" rel="noopener noreferrer">
          –ü–æ—Å–µ—Ç–∏—Ç—å —Å–∞–π—Ç
        </a>
      )}

      {showMutedNotice && muted && (
        <div className="video__mutedNotice">üîá –í–∏–¥–µ–æ –±–µ–∑ –∑–≤—É–∫–∞. –ù–∞–∂–º–∏—Ç–µ –¥–ª—è –≤–∫–ª—é—á–µ–Ω–∏—è.</div>
      )}

      <div className="video__title">{title}</div>
    </div>
  );
};

export default Video;
