import React, { useRef, useEffect, useState } from "react";
import "./Video.css";

function formatCount(countStr) {
  let n = typeof countStr === "string"
    ? parseFloat(countStr.replace(/[^\d.]/g, "")) || 0
    : Number(countStr) || 0;
  if (n >= 1_000_000) return (n / 1_000_000).toFixed(1) + "M";
  if (n >= 1_000) return (n / 1_000).toFixed(1) + "K";
  return n.toString();
}

export default function Video({
  url,
  channel,
  description,
  likes,
  dislikes,
  comments,
  isAd = false,
  link,
  isActive,
  onActivate
}) {
  const ref = useRef();
  const [muted, setMuted] = useState(true);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const v = ref.current;
    const obs = new IntersectionObserver(
      ([e]) => {
        if (e.isIntersecting && isActive) v.play().catch(() => {});
        else v.pause();
      },
      { threshold: 0.6 }
    );
    obs.observe(v);
    return () => obs.disconnect();
  }, [isActive]);

  return (
    <div className="video" onClick={onActivate}>
      <video
        ref={ref}
        className="video__player"
        src={url}
        loop
        muted={muted}
        playsInline
        onCanPlay={() => setLoading(false)}
      />
      {loading && <div className="video__loading">Wird geladenâ€¦</div>}

      <button
        className="video__muteBtn"
        onClick={e => {
          e.stopPropagation();
          setMuted(!muted);
        }}
      >
        <img
          src={process.env.PUBLIC_URL + (muted ? "/icons/mute.svg" : "/icons/sound.svg")}
          alt={muted ? "Muted" : "Sound on"}
        />
      </button>

      <div className="video__sidebar">
        <div className="video__button" onClick={e => e.stopPropagation()}>
          <img src={process.env.PUBLIC_URL + "/icons/like.svg"} alt="Like" />
          <span>{formatCount(likes)}</span>
        </div>
        <div className="video__button" onClick={e => e.stopPropagation()}>
          <img src={process.env.PUBLIC_URL + "/icons/dislike.svg"} alt="Dislike" />
          <span>{formatCount(dislikes)}</span>
        </div>
        <div className="video__button" onClick={e => e.stopPropagation()}>
          <img src={process.env.PUBLIC_URL + "/icons/comment.svg"} alt="Comment" />
          <span>{formatCount(comments)}</span>
        </div>
        <div className="video__button" onClick={e => e.stopPropagation()}>
          <img src={process.env.PUBLIC_URL + "/icons/share.svg"} alt="Share" />
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          <button>Abonnieren</button>
        </div>
        <div className="video__description">{description}</div>
        {isAd && link && (
          <a
            className="video__adLink"
            href={link}
            target="_blank"
            rel="noopener noreferrer"
          >
            ðŸ‘‰ Hier klicken
          </a>
        )}
      </div>
    </div>
  );
}
