import React, { useEffect, useRef, useState } from "react";
import "../styles/Video.css";

function Video({ src, channel, description, likes = 0, messages = 0, shares = 0, adLink }) {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(true);
  const [showMutedMsg, setShowMutedMsg] = useState(false);

  useEffect(() => {
    const video = videoRef.current;
    if (video) {
      video.muted = isMuted;
      const playPromise = video.play();
      if (playPromise !== undefined) {
        playPromise.catch((err) => {
          console.log("Autoplay blocked:", err);
        });
      }
    }
  }, [src, isMuted]);

  const handleClick = (e) => {
    const clickedOnControl = e.target.closest(".video__button, .video__footer, a");
    if (!clickedOnControl) {
      setIsMuted((prev) => {
        const next = !prev;
        if (next) {
          setShowMutedMsg(true);
          setTimeout(() => setShowMutedMsg(false), 1500);
        }
        return next;
      });
    }
  };

  return (
    <div className="video" onClick={handleClick}>
      <video
        ref={videoRef}
        className="video__player"
        src={src}
        autoPlay
        playsInline
        muted={isMuted}
        loop
      />
      
      {/* –ú—É—Ç-—Å–æ–æ–±—â–µ–Ω–∏–µ */}
      {showMutedMsg && (
        <div className="video__loading">üîá –í–∏–¥–µ–æ –≤ –±–µ–∑–∑–≤—É—á–Ω–æ–º —Ä–µ–∂–∏–º–µ</div>
      )}

      {/* Sidebar */}
      <div className="video__sidebar">
        <div className="video__button">
          <img src="/icons/like.svg" alt="Like" width={30} height={30} />
          <span>{likes}</span>
        </div>
        <div className="video__button">
          <img src="/icons/comment.svg" alt="Comment" width={30} height={30} />
          <span>{messages}</span>
        </div>
        <div className="video__button">
          <img src="/icons/share.svg" alt="Share" width={30} height={30} />
          <span>{shares}</span>
        </div>
      </div>

      {/* Footer */}
      <div className="video__footer">
        <div className="video__channel">
          <strong>@{channel}</strong>
          <button>Follow</button>
        </div>
        <div className="video__description">{description}</div>
        {adLink && (
          <a className="video__adLink" href={adLink} target="_blank" rel="noreferrer">
            –ü–µ—Ä–µ–π—Ç–∏ –ø–æ —Å—Å—ã–ª–∫–µ
          </a>
        )}
      </div>
    </div>
  );
}

export default Video;
