import React, { useRef, useState, useEffect } from "react";
import "./Video.css";

function Video({ url, channel, description, likes, messages, shares, isAd, adLink }) {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(false);
  const [showMutedMessage, setShowMutedMessage] = useState(false);

  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          video.play().catch((err) => {
            console.warn("Autoplay failed:", err);
          });
        } else {
          video.pause();
        }
      },
      { threshold: 0.75 }
    );

    observer.observe(video);

    return () => observer.unobserve(video);
  }, []);

  const handleVideoClick = (e) => {
    if (
      e.target.closest(".video__sidebar") ||
      e.target.closest(".video__footer")
    )
      return;

    const video = videoRef.current;
    if (!video) return;

    const nextMuted = !video.muted;
    video.muted = nextMuted;
    setIsMuted(nextMuted);

    if (nextMuted) {
      setShowMutedMessage(true);
      setTimeout(() => setShowMutedMessage(false), 1500);
    }
  };

  return (
    <div className="video" onClick={handleVideoClick}>
      <video
        className="video__player"
        ref={videoRef}
        src={url}
        playsInline
        loop
        muted={false}
        preload="auto"
      />

      <div className="video__sidebar">
  <div className="video__button">
    <span className="material-symbols-outlined">thumb_up</span>
    <span>{likes}</span>
  </div>
  <div className="video__button">
    <span className="material-symbols-outlined">thumb_down</span>
  </div>
  <div className="video__button">
    <span className="material-symbols-outlined">comment</span>
  </div>
  <div className="video__button">
    <span className="material-symbols-outlined">share</span>
  </div>
</div>



      <div className="video__footer">
  {!isAd && (
    <>
      <div className="video__channel">
        <strong>@{channel}</strong>
        <button>Abonnieren</button>
      </div>
      <div className="video__description">{description}</div>
    </>
  )}

  {isAd && (
    <>
      {/* Описание без слова "Реклама" */}
      <div className="video__description">{description}</div>

      {/* Центрированная кнопка регистрации */}
      <a
        href={adLink || "#"}
        target="_blank"
        rel="noopener noreferrer"
        className="video__register-button centered"
      >
        Jetzt registrieren
      </a>
    </>
  )}
</div>



      {showMutedMessage && <div className="video__loading">🔇 Ton ist aus</div>}
    </div>
  );
}

export default Video;
