import React, { useRef, useEffect, useState } from 'react';
import './Videos.css';

export default function Video({ data, isActive }) {
  const { video, description, likes, channel, avatar } = data;
  const ref = useRef();
  const [muted, setMuted] = useState(true);
  const [count, setCount] = useState(
    () => parseInt(localStorage.getItem(`likes_${data.id}`)) || likes
  );
  const [liked, setLiked] = useState(
    () => !!localStorage.getItem(`liked_${data.id}`)
  );

  // –ê–≤—Ç–æ-–ø–ª–µ–π/–ø–∞—É–∑–∞
  useEffect(() => {
    if (isActive) {
      ref.current.play().catch(() => {});
    } else {
      ref.current.pause();
      ref.current.currentTime = 0;
    }
  }, [isActive]);

  // –õ–∞–π–∫
  const onLike = () => {
    const next = liked ? count - 1 : count + 1;
    setLiked(!liked);
    setCount(next);
    localStorage.setItem(`likes_${data.id}`, next);
    liked
      ? localStorage.removeItem(`liked_${data.id}`)
      : localStorage.setItem(`liked_${data.id}`, '1');
  };

  // –®–∞—Ä–∏–Ω–≥
  const onShare = () => {
    const shareData = { text: description, url: window.location.href };
    navigator.share
      ? navigator.share(shareData).catch(() => {})
      : (navigator.clipboard.writeText(window.location.href) && alert('Link kopiert!'));
  };

  return (
    <div className="video-wrapper">
      <video
        ref={ref}
        className="video-player"
        src={video}
        muted={muted}
        loop
        playsInline
        onClick={() => setMuted(!muted)}
      />
      <div className="video-sidebar">
        <div className={`icon like ${liked ? 'active' : ''}`} onClick={onLike}>
          ‚ù§Ô∏è<span>{count}</span>
        </div>
        <div className="icon comment" onClick={() => alert('Kommentare nicht verf√ºgbar')}>
          üí¨
        </div>
        <div className="icon share" onClick={onShare}>
          üîó
        </div>
      </div>
      <div className="video-info">
        <div className="channel">
          <img src={avatar} alt="Avatar" />
          <div className="channel-text">
            <p className="channel-name">{channel}</p>
            <button className="subscribe">Abonnieren</button>
          </div>
        </div>
        <p className="description">{description}</p>
      </div>
    </div>
  );
}
