import React, { useRef, useState, useEffect } from "react";
import "./Video.css";

function Video({
  url,
  channel,
  description,
  likes,
  comments,
  shares,
  isAd,
  adLink,
  poster,
}) {
  const videoRef = useRef(null);
  const [isActive, setIsActive] = useState(false);
  const [isMutedByBrowser, setIsMutedByBrowser] = useState(false);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        const isVisible = entry.isIntersecting;
        setIsActive(isVisible);
        const video = videoRef.current;

        if (video) {
          if (isVisible) {
            // –í–∫–ª—é—á–∞–µ–º –∑–≤—É–∫ –∏ –ø—ã—Ç–∞–µ–º—Å—è –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ—Å—Ç–∏
            video.muted = false;
            const playPromise = video.play();

            if (playPromise !== undefined) {
              playPromise
                .then(() => {
                  setIsMutedByBrowser(false);
                })
                .catch((error) => {
                  console.warn("–ë—Ä–∞—É–∑–µ—Ä –∑–∞–ø—Ä–µ—Ç–∏–ª –∑–≤—É–∫:", error);
                  video.muted = true;
                  video.play();
                  setIsMutedByBrowser(true);
                });
            }
          } else {
            video.pause();
          }
        }
      },
      { threshold: 0.8 }
    );

    const el = videoRef.current;
    if (el) observer.observe(el);

    return () => {
      if (el) observer.unobserve(el);
    };
  }, []);

  return (
    <div className="video">
      <video
        ref={videoRef}
        className="video__player"
        src={url}
        poster={poster}
        playsInline
        loop
        preload="auto"
      />

      {isMutedByBrowser && (
        <div className="video__muted-notice">
          üîá –ë—Ä–∞—É–∑–µ—Ä –æ—Ç–∫–ª—é—á–∏–ª –∑–≤—É–∫ –∞–≤—Ç–æ–º–∞—Ç–∏—á–µ—Å–∫–∏
        </div>
      )}

      <div className="video__sidebar">
        <div className="video__button">
          <span className="material-symbols-outlined">thumb_up</span>
          <span>{likes}</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined">thumb_down</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined">comment</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined">share</span>
        </div>
      </div>

      <div className="video__footer">
        {!isAd ? (
          <>
            <div className="video__channel">
              <strong>{channel}</strong>
              <button>Abonnieren</button>
            </div>
            <div className="video__description">{description}</div>
          </>
        ) : (
          <>
            <div className="video__description">{description}</div>
            <a
              href={adLink || "#"}
              target="_blank"
              rel="noopener noreferrer"
              className="video__register-button centered"
            >
              Jetzt registrieren
            </a>
          </>
        )}
      </div>
    </div>
  );
}

export default Video;
