import React, { useEffect, useRef, useState } from "react";
import "./Video.css";

function Video({ url, channel, description, likes, messages, shares, isAd, adLink }) {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(false);
  const [showMutedMessage, setShowMutedMessage] = useState(false);
  const observerRef = useRef(null);

  // Автоматически включает/выключает видео при появлении на экране
  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        const video = videoRef.current;
        if (!video) return;

        if (entry.isIntersecting) {
          video.play().catch((err) => {
            console.warn("Автоплей не сработал:", err);
          });
        } else {
          video.pause();
        }
      },
      { threshold: 0.75 }
    );

    if (videoRef.current) {
      observer.observe(videoRef.current);
      observerRef.current = observer;
    }

    return () => {
      if (observerRef.current && videoRef.current) {
        observerRef.current.unobserve(videoRef.current);
      }
    };
  }, []);

  // Клик по видео включает/выключает звук
  const handleVideoClick = (e) => {
    if (e.target.closest(".video__sidebar") || e.target.closest(".video__footer")) return;

    const video = videoRef.current;
    if (!video) return;

    const nextMuted = !video.muted;
    video.muted = nextMuted;
    setIsMuted(nextMuted);

    if (nextMuted) {
      setShowMutedMessage(true);
      setTimeout(() => setShowMutedMessage(false), 1500);
    }
  };

  return (
    <div className="video" onClick={handleVideoClick}>
      <video
        ref={videoRef}
        className="video__player"
        src={url}
        playsInline
        muted={false}
        loop
        preload="auto"
      />

      {/* Sidebar */}
      <div className="video__sidebar">
        <div className="video__button">
          <img src="/icons/like.svg" alt="Like" width={30} height={30} />
          <span>{likes}</span>
        </div>
        <div className="video__button">
          <img src="/icons/comment.svg" alt="Comment" width={30} height={30} />
          <span>{messages}</span>
        </div>
        <div className="video__button">
          <img src="/icons/share.svg" alt="Share" width={30} height={30} />
          <span>{shares}</span>
        </div>
      </div>

      {/* Footer */}
      <div className="video__footer">
        <div className="video__channel">
          <strong>@{channel}</strong>
          <button>Подписаться</button>
        </div>
        <div className="video__description">{description}</div>
        {isAd && adLink && (
          <a href={adLink} target="_blank" rel="noopener noreferrer" className="video__adLink">
            Подробнее
          </a>
        )}
      </div>

      {/* Сообщение "Видео в муте" */}
      {showMutedMessage && (
        <div className="video__loading">ВИДЕО БЕЗ ЗВУКА 🔇</div>
      )}
    </div>
  );
}

export default Video;
