import React, { useRef, useState, useEffect } from "react";
import "./Video.css";

function formatCount(count) {
  if (typeof count === "string") {
    if (count.includes("M")) return parseFloat(count) * 1_000_000;
    if (count.includes("K")) return parseFloat(count) * 1_000;
    return parseFloat(count);
  }
  return count;
}

function formatDisplay(count) {
  if (count >= 1_000_000) return (count / 1_000_000).toFixed(1) + "M";
  if (count >= 1_000) return (count / 1_000).toFixed(1) + "K";
  return count;
}

function Video({ id, url, channel, description, likes = 0, dislikes = 0, comments = 0, isActive, onActivate }) {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(true);
  const [likeCount, setLikeCount] = useState(formatCount(likes));
  const [dislikeCount, setDislikeCount] = useState(formatCount(dislikes));
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);

  const toggleMute = () => {
    if (videoRef.current) {
      videoRef.current.muted = !videoRef.current.muted;
      setIsMuted(videoRef.current.muted);
    }
  };

  // ‚ñ∂Ô∏è –£–ø—Ä–∞–≤–ª–µ–Ω–∏–µ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏–µ–º –≤ –∑–∞–≤–∏—Å–∏–º–æ—Å—Ç–∏ –æ—Ç isActive
  useEffect(() => {
    if (videoRef.current) {
      if (isActive) {
        videoRef.current.play().catch(() => {});
      } else {
        videoRef.current.pause();
      }
    }
  }, [isActive]);

  const onLike = () => {
    if (isLiked) {
      setLikeCount((prev) => prev - 1);
      setIsLiked(false);
    } else {
      setLikeCount((prev) => prev + 1);
      setIsLiked(true);
      if (isDisliked) {
        setDislikeCount((prev) => prev - 1);
        setIsDisliked(false);
      }
    }
  };

  const onDislike = () => {
    if (isDisliked) {
      setDislikeCount((prev) => prev - 1);
      setIsDisliked(false);
    } else {
      setDislikeCount((prev) => prev + 1);
      setIsDisliked(true);
      if (isLiked) {
        setLikeCount((prev) => prev - 1);
        setIsLiked(false);
      }
    }
  };

  const onComment = () => {
    alert("–û—Ç–∫—Ä—ã—Ç–∏–µ –∫–æ–º–º–µ–Ω—Ç–∞—Ä–∏–µ–≤ ‚Äî –≤ —Ä–∞–∑—Ä–∞–±–æ—Ç–∫–µ");
  };

  const onShare = () => {
    alert("–°—Å—ã–ª–∫–∞ —Å–∫–æ–ø–∏—Ä–æ–≤–∞–Ω–∞!");
  };

  return (
    <div className="video" onClick={() => { onActivate(); toggleMute(); }}>
      <video
        ref={videoRef}
        className="video__player"
        src={url}
        muted={isMuted}
        loop
        playsInline
        controls={false}
      />

      <div className="video__sidebar">
        <div className={`video__button ${isLiked ? "active" : ""}`} onClick={(e) => { e.stopPropagation(); onLike(); }}>
          <img src="/icons/like.svg" alt="Like" />
          <span>{formatDisplay(likeCount)}</span>
        </div>
        <div className={`video__button ${isDisliked ? "active" : ""}`} onClick={(e) => { e.stopPropagation(); onDislike(); }}>
          <img src="/icons/dislike.svg" alt="Dislike" />
          <span>{formatDisplay(dislikeCount)}</span>
        </div>
        <div className="video__button" onClick={(e) => { e.stopPropagation(); onComment(); }}>
          <img src="/icons/comment.svg" alt="Comment" />
          <span>{comments}</span>
        </div>
        <div className="video__button" onClick={(e) => { e.stopPropagation(); onShare(); }}>
          <img src="/icons/share.svg" alt="Share" />
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          <button>–ü–æ–¥–ø–∏—Å–∞—Ç—å—Å—è</button>
        </div>
        <div className="video__description">{description}</div>
      </div>

      <div className="video__mute-icon">
        {isMuted ? "üîá" : "üîä"}
      </div>
    </div>
  );
}

export default Video;
