import React, { useRef, useEffect } from "react";
import "../App.css";

function Videos({ id, src, autoPlay, loop }) {
  const videoRef = useRef(null);

  useEffect(() => {
    const video = videoRef.current;
    if (autoPlay && video) {
      video.play().catch((err) => console.error("Ошибка при автозапуске:", err));
    } else if (video) {
      video.pause();
    }
  }, [autoPlay]);

  return (
    <video
      id={id}
      className="video"
      ref={videoRef}
      src={src}
      autoPlay={autoPlay}
      loop={loop}
      muted
      playsInline
    />
  );
}

export default Videos;
