      comments: "9.2K",
    },
    {
      id: 4,
      url: process.env.PUBLIC_URL + "/videos/fourth.mp4",
      channel: "@foodiesunite",
      description: "Making street tacos from scratch ðŸŒ®",
      likes: "2.3M",
      dislikes: "65K",
      comments: "5.4K",
    },
  ];

  return (
    <div className="app">
      {videos.map((video) => (
        <Video
          key={video.id}
          {...video}
          id={video.id}
          isActive={activeId === video.id}
          onActivate={() => setActiveId(video.id)}
        />
      ))}
    </div>
  );
}

export default App;
