import React, { useRef, useEffect, useState } from "react";
import "./Video.css";

function Video({ 
  url, 
  channel, 
  description, 
  likes, 
  isActive, 
  onActivate,
  isAd,
  link 
}) {
  const videoRef = useRef(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const handleCanPlay = () => {
      console.log('Video can play:', url);
      setIsLoading(false);
    };

    const handleError = (e) => {
      console.error('Video error:', e);
      setError('Ошибка загрузки видео');
      setIsLoading(false);
    };

    const handleLoadStart = () => {
      console.log('Video loading started:', url);
      setIsLoading(true);
    };

    video.addEventListener('loadstart', handleLoadStart);
    video.addEventListener('canplay', handleCanPlay);
    video.addEventListener('error', handleError);

    return () => {
      video.removeEventListener('loadstart', handleLoadStart);
      video.removeEventListener('canplay', handleCanPlay);
      video.removeEventListener('error', handleError);
    };
  }, [url]);

  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    if (isActive) {
      console.log('Playing video:', url);
      video.currentTime = 0;
      video.play().catch(e => {
        console.log('Autoplay blocked:', e.message);
      });
    } else {
      video.pause();
    }
  }, [isActive, url]);

  const handleVideoClick = () => {
    const video = videoRef.current;
    if (!video) return;

    if (video.paused) {
      video.play();
    } else {
      video.pause();
    }
  };

  return (
    <div className="video" onClick={onActivate}>
      <video
        ref={videoRef}
        className="video__player"
        src={url}
        loop
        muted
        playsInline
        preload="metadata"
        onClick={handleVideoClick}
        style={{
          width: '100%',
          height: '100%',
          objectFit: 'cover',
          background: '#000'
        }}
      />

      {isLoading && (
        <div className="video__loading">
          <div>Загрузка видео...</div>
          <div style={{fontSize: '12px', marginTop: '10px'}}>
            {url}
          </div>
        </div>
      )}

      {error && (
        <div className="video__loading" style={{color: 'red'}}>
          <div>{error}</div>
          <div style={{fontSize: '12px'}}>
            {url}
          </div>
        </div>
      )}

      <div className="video__sidebar">
        <div className="video__button">
          <span className="material-symbols-outlined">favorite</span>
          <span>{likes}</span>
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <strong style={{color: 'white'}}>{channel}</strong>
        </div>
        <div className="video__description" style={{color: 'white'}}>
          {description}
        </div>
      </div>
    </div>
  );
}

export default Video;
