import React, { useState, useRef, useEffect } from "react";
import "./Video.css";

function formatCount(count) {
  if (typeof count === "string") {
    count = parseFloat(count.replace(/[^\d.]/g, ""));
    if (isNaN(count)) return "0";
  }
  if (count >= 1_000_000) return (count / 1_000_000).toFixed(1) + "M";
  if (count >= 1_000) return (count / 1_000).toFixed(1) + "K";
  return count.toString();
}

function Video({ url, channel, description, likes = 0, dislikes = 0, comments = 0 }) {
  const [likeCount, setLikeCount] = useState(Number(likes) || 0);
  const [dislikeCount, setDislikeCount] = useState(Number(dislikes) || 0);
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);
  const videoRef = useRef(null);

  const onLike = () => {
    setIsLiked(prev => {
      if (!prev) {
        setLikeCount(c => c + 1);
        if (isDisliked) {
          setIsDisliked(false);
          setDislikeCount(c => c - 1);
        }
      } else {
        setLikeCount(c => c - 1);
      }
      return !prev;
    });
  };

  const onDislike = () => {
    setIsDisliked(prev => {
      if (!prev) {
        setDislikeCount(c => c + 1);
        if (isLiked) {
          setIsLiked(false);
          setLikeCount(c => c - 1);
        }
      } else {
        setDislikeCount(c => c - 1);
      }
      return !prev;
    });
  };

  const onComment = () => {
    alert("Kommentare werden geladen...");
  };

  const onShare = () => {
    navigator.clipboard.writeText(window.location.href);
    alert("Link wurde kopiert!");
  };

  const toggleMute = () => {
    if (videoRef.current) {
      videoRef.current.muted = !videoRef.current.muted;
    }
  };

  // Автостоп других видео
  useEffect(() => {
    const handleScroll = () => {
      if (videoRef.current) {
        const rect = videoRef.current.getBoundingClientRect();
        const isVisible = rect.top >= 0 && rect.bottom <= window.innerHeight;
        videoRef.current.muted = !isVisible;
        videoRef.current.pause();
        if (isVisible) {
          videoRef.current.play().catch(() => {});
        }
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="video" onClick={toggleMute}>
      <video
        ref={videoRef}
        className="video__player"
        src={url}
        autoPlay
        loop
        muted
        playsInline
      ></video>

      <div className="video__sidebar">
        <div className={`video__button ${isLiked ? "active" : ""}`} onClick={onLike}>
          <img src="/icons/like.svg" alt="Gefällt mir" />
          <span>{formatCount(likeCount)}</span>
        </div>
        <div className={`video__button ${isDisliked ? "active" : ""}`} onClick={onDislike}>
          <img src="/icons/dislike.svg" alt="Gefällt mir nicht" />
          <span>{formatCount(dislikeCount)}</span>
        </div>
        <div className="video__button" onClick={onComment}>
          <img src="/icons/comment.svg" alt="Kommentare" />
          <span>{formatCount(comments)}</span>
        </div>
        <div className="video__button" onClick={onShare}>
          <img src="/icons/share.svg" alt="Teilen" />
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          <button>Abonnieren</button>
        </div>
        <div className="video__description">{description}</div>
      </div>
    </div>
  );
}

export default Video;
