import React, { useRef, useEffect, useState } from "react";
import "./Video.css";

function parseCount(str) {
  if (typeof str === "string") {
    if (str.includes("M")) return Math.round(parseFloat(str) * 1_000_000);
    if (str.includes("K")) return Math.round(parseFloat(str) * 1_000);
    return parseFloat(str);
  }
  return str;
}

function formatDisplay(num) {
  if (num >= 1_000_000) return (num / 1_000_000).toFixed(1) + "M";
  if (num >= 1_000) return (num / 1_000).toFixed(1) + "K";
  return num.toString();
}

function Video({ url, channel, description, likes, dislikes, comments, isActive, onActivate }) {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(true);
  const [likeCount, setLikeCount] = useState(parseCount(likes));
  const [dislikeCount, setDislikeCount] = useState(parseCount(dislikes));
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);

  // Play/pause depending on active
  useEffect(() => {
    if (!videoRef.current) return;
    if (isActive) {
      videoRef.current.play().catch(() => {});
    } else {
      videoRef.current.pause();
      videoRef.current.currentTime = 0;
    }
  }, [isActive]);

  const toggleMute = () => {
    if (videoRef.current) {
      videoRef.current.muted = !videoRef.current.muted;
      setIsMuted(videoRef.current.muted);
    }
  };

  const onLike = (e) => {
    e.stopPropagation();
    if (isLiked) {
      setLikeCount((prev) => prev - 1);
      setIsLiked(false);
    } else {
      setLikeCount((prev) => prev + 1);
      setIsLiked(true);
      if (isDisliked) {
        setDislikeCount((prev) => prev - 1);
        setIsDisliked(false);
      }
    }
  };

  const onDislike = (e) => {
    e.stopPropagation();
    if (isDisliked) {
      setDislikeCount((prev) => prev - 1);
      setIsDisliked(false);
    } else {
      setDislikeCount((prev) => prev + 1);
      setIsDisliked(true);
      if (isLiked) {
        setLikeCount((prev) => prev - 1);
        setIsLiked(false);
      }
    }
  };

  const onComment = (e) => {
    e.stopPropagation();
    alert("Открытие комментариев — в разработке");
  };

  const onShare = (e) => {
    e.stopPropagation();
    alert("Ссылка скопирована!");
  };

  return (
    <div className="video" onClick={() => { onActivate(); toggleMute(); }}>
      <video
        ref={videoRef}
        className="video__player"
        src={url}
        muted={isMuted}
        loop
        playsInline
        controls={false}
      />

      <div className="video__sidebar">
        <div className={`video__button ${isLiked ? "active" : ""}`} onClick={onLike}>
          <img src="/icons/like.svg" alt="Like" />
          <span>{formatDisplay(likeCount)}</span>
        </div>
        <div className={`video__button ${isDisliked ? "active" : ""}`} onClick={onDislike}>
          <img src="/icons/dislike.svg" alt="Dislike" />
          <span>{formatDisplay(dislikeCount)}</span>
        </div>
        <div className="video__button" onClick={onComment}>
          <img src="/icons/comment.svg" alt="Comment" />
          <span>{comments}</span>
        </div>
        <div className="video__button" onClick={onShare}>
          <img src="/icons/share.svg" alt="Share" />
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          <button>Подписаться</button>
        </div>
        <div className="video__description">{description}</div>
      </div>
    </div>
  );
}

export default Video;
