import React, { useEffect, useRef } from "react";
import "./Video.css";

function Video({ url, poster, channel, description, likes, comments, isAd, link, isActive, onActivate }) {
  const videoRef = useRef(null);

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          onActivate();
        }
      },
      { threshold: 0.6 }
    );

    const el = videoRef.current;
    if (el) observer.observe(el);

    return () => {
      if (el) observer.unobserve(el);
    };
  }, [onActivate]);

  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    if (isActive) {
      video.muted = true;
      const playPromise = video.play();
      if (playPromise !== undefined) {
        playPromise.catch((e) => console.warn("Autoplay blocked:", e));
      }
    } else {
      video.pause();
    }
  }, [isActive]);

  return (
    <div className="video">
      <video
        ref={videoRef}
        className="video__player"
        src={isActive ? url : ""}
        poster={poster}
        playsInline
        muted
        loop
        preload={isActive ? "auto" : "none"}
      />

      <div className="video__sidebar">
        <div className="video__button">üëç <span>{likes}</span></div>
        <div className="video__button">üí¨ <span>{comments}</span></div>
      </div>

      <div className="video__footer">
        {!isAd ? (
          <>
            <strong>{channel}</strong>
            <div>{description}</div>
          </>
        ) : (
          <>
            <div>{description}</div>
            <a
              className="video__register-button"
              href={link}
              target="_blank"
              rel="noopener noreferrer"
            >
              Jetzt registrieren
            </a>
          </>
        )}
      </div>
    </div>
  );
}

export default Video;
