// src/components/Video.js
import React, { useState, useEffect } from "react";
import "./Video.css";

function formatCount(count) {
  if (count >= 1_000_000) return (count / 1_000_000).toFixed(1) + "M";
  if (count >= 1_000) return (count / 1_000).toFixed(1) + "K";
  return count;
}

function Video({ url, channel, description, likes, dislikes, comments }) {
  const [likeCount, setLikeCount] = useState(likes);
  const [dislikeCount, setDislikeCount] = useState(dislikes);
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);
  const [showComments, setShowComments] = useState(false);

  useEffect(() => {
    const stored = JSON.parse(localStorage.getItem(url)) || {};
    setIsLiked(stored.isLiked || false);
    setIsDisliked(stored.isDisliked || false);
    setLikeCount(stored.likeCount || likes);
    setDislikeCount(stored.dislikeCount || dislikes);
  }, [url, likes, dislikes]);

  useEffect(() => {
    localStorage.setItem(
      url,
      JSON.stringify({ isLiked, isDisliked, likeCount, dislikeCount })
    );
  }, [isLiked, isDisliked, likeCount, dislikeCount, url]);

  const handleLike = () => {
    if (isLiked) {
      setLikeCount((c) => c - 1);
      setIsLiked(false);
    } else {
      setLikeCount((c) => c + 1);
      setIsLiked(true);
      if (isDisliked) {
        setDislikeCount((c) => c - 1);
        setIsDisliked(false);
      }
    }
  };

  const handleDislike = () => {
    if (isDisliked) {
      setDislikeCount((c) => c - 1);
      setIsDisliked(false);
    } else {
      setDislikeCount((c) => c + 1);
      setIsDisliked(true);
      if (isLiked) {
        setLikeCount((c) => c - 1);
        setIsLiked(false);
      }
    }
  };

  return (
    <div className="video">
      <video
        className="video__player"
        src={url}
        autoPlay
        loop
        muted
        playsInline
      ></video>

      <div className="video__actions">
        <div className={`video__button ${isLiked ? "active" : ""}`} onClick={handleLike}>
          <img src="/icons/like.svg" alt="like" />
          <span>{formatCount(likeCount)}</span>
        </div>

        <div className={`video__button ${isDisliked ? "active" : ""}`} onClick={handleDislike}>
          <img src="/icons/dislike.svg" alt="dislike" />
          <span>{formatCount(dislikeCount)}</span>
        </div>

        <div className="video__button" onClick={() => setShowComments(true)}>
          <img src="/icons/comment.svg" alt="comment" />
          <span>{formatCount(comments)}</span>
        </div>

        <div className="video__button">
          <img src="/icons/share.svg" alt="share" />
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          <button>Подписаться</button>
        </div>
        <div className="video__description">{description}</div>
      </div>

      {showComments && (
        <div className="video__modal" onClick={() => setShowComments(false)}>
          <div className="video__modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>Комментарии</h3>
            <p>Здесь будут комментарии</p>
            <button onClick={() => setShowComments(false)}>Закрыть</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Video;
