import React, { useEffect, useRef, useState } from "react";
import "./Video.css";

function Video({
  url,
  channel,
  description,
  likes,
  dislikes,
  comments,
  isAd,
  adLink,
  isActive
}) {
  const videoRef = useRef(null);
  const [showMutedMessage, setShowMutedMessage] = useState(false);

 useEffect(() => {
  const video = videoRef.current;
  if (!video) return;

  if (isActive) {
    video.muted = false;
    const playPromise = video.play();
    if (playPromise !== undefined) {
      playPromise.catch((err) => {
        console.warn("Autoplay error:", err);
        video.muted = true;
        video.play();
        setShowMutedMessage(true);
        setTimeout(() => setShowMutedMessage(false), 1500);
      });
    }
  } else {
    video.pause();
    video.currentTime = 0; // Сброс к началу
  }
}, [isActive]);


  const handleClick = (e) => {
    if (
      e.target.closest(".video__sidebar") ||
      e.target.closest(".video__footer")
    ) {
      return;
    }

    const video = videoRef.current;
    if (!video) return;

    const nextMuted = !video.muted;
    video.muted = nextMuted;

    if (nextMuted) {
      setShowMutedMessage(true);
      setTimeout(() => setShowMutedMessage(false), 1500);
    }
  };

  return (
    <div className="video" onClick={handleClick}>
      <video
        className="video__player"
        ref={videoRef}
        src={url}
        loop
        playsInline
        preload="auto"
        muted={!isActive}
      />

      <div className="video__sidebar">
        <div className="video__button">
          <span className="material-symbols-outlined" style={{ fontSize: "36px" }}>
            thumb_up
          </span>
          <span>{likes}</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined" style={{ fontSize: "36px" }}>
            thumb_down
          </span>
          <span>{dislikes}</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined" style={{ fontSize: "36px" }}>
            comment
          </span>
          <span>{comments}</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined" style={{ fontSize: "36px" }}>
            share
          </span>
        </div>
      </div>

      <div className="video__footer">
        {!isAd && (
          <>
            <div className="video__channel">
              <strong>@{channel}</strong>
              <button>Abonnieren</button>
            </div>
            <div className="video__description">{description}</div>
          </>
        )}
        {isAd && (
          <>
            <div className="video__description">{description}</div>
            <a
              href={adLink || "#"}
              target="_blank"
              rel="noopener noreferrer"
              className="video__register-button centered"
            >
              Jetzt registrieren
            </a>
          </>
        )}
      </div>

      {showMutedMessage && (
        <div className="video__loading">🔇 Ton ist aus</div>
      )}
    </div>
  );
}

export default Video;
