import React, { useState } from "react";
import Video from "./components/Video";
import BottomNav from "./components/BottomNav.js";
import "./App.css";

function App() {
  const [activeId, setActiveId] = useState(1);

  const videos = [
    {
      id: 1,
      url: process.env.PUBLIC_URL + "/videos/first.mp4",
      channel: "Канал Eins",
      description: "Erstes Video – kurze Beschreibung",
      likes: "2.1M",
      dislikes: "123K",
      comments: "13.5K",
      isAd: false,
    },
    {
      id: 2,
      url: process.env.PUBLIC_URL + "/videos/second.mp4",
      channel: "@boatsdotcom",
      description: "Check out the secret cabin on this Adre...",
      likes: "2M",
      dislikes: "101K",
      comments: "7.5K",
      isAd: false,
    },
    {
      id: 3,
      url: process.env.PUBLIC_URL + "/videos/ad.mp4",
      channel: "Ad Channel",
      description: "Зарегистрируйтесь и получите бонус!",
      likes: "0",
      dislikes: "0",
      comments: "0",
      isAd: true,
    },
  ];

  return (
    <div className="app">
      {videos.map((video) => (
        <Video
          key={video.id}
          {...video}
          isActive={activeId === video.id}
          onActivate={() => setActiveId(video.id)}
        />
      ))}
      <BottomNav />
    </div>
  );
}

export default App;
