import React, { useState } from "react";
import "./Video.css";

function formatNumber(num) {
  if (num >= 1000000) return (num / 1000000).toFixed(1) + "M";
  if (num >= 1000) return (num / 1000).toFixed(1) + "K";
  return num.toString();
}

function Video({ url, channel, description, likes, dislikes, comments }) {
  const [likeCount, setLikeCount] = useState(likes);
  const [dislikeCount, setDislikeCount] = useState(dislikes);
  const [commentCount] = useState(comments);

  const onLike = () => setLikeCount(prev => prev + 1);
  const onDislike = () => setDislikeCount(prev => prev + 1);

  return (
    <div className="video">
      <video
        className="video__player"
        src={url}
        autoPlay
        loop
        muted
        playsInline
      ></video>

      <div className="video__buttons">
        <div className="video__button" onClick={onLike}>
          <img src="/icons/like.svg" alt="Like" />
          <span>{formatNumber(likeCount)}</span>
        </div>
        <div className="video__button" onClick={onDislike}>
          <img src="/icons/dislike.svg" alt="Dislike" />
          <span>{formatNumber(dislikeCount)}</span>
        </div>
        <div className="video__button">
          <img src="/icons/comment.svg" alt="Comment" />
          <span>{formatNumber(commentCount)}</span>
        </div>
        <div className="video__button">
          <img src="/icons/share.svg" alt="Share" />
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          <button>Подписаться</button>
        </div>
        <div className="video__description">{description}</div>
      </div>
    </div>
  );
}

export default Video;
