import React, { useRef, useState, useEffect } from "react";
import "./Video.css";

function Video({ url, channel, description, likes, messages, shares, isAd, adLink }) {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(false);
  const [showMutedMessage, setShowMutedMessage] = useState(false);

  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          video.play().catch((err) => {
            console.warn("Autoplay failed:", err);
          });
        } else {
          video.pause();
        }
      },
      { threshold: 0.75 }
    );

    observer.observe(video);

    return () => observer.unobserve(video);
  }, []);

  const handleVideoClick = (e) => {
    if (
      e.target.closest(".video__sidebar") ||
      e.target.closest(".video__footer")
    )
      return;

    const video = videoRef.current;
    if (!video) return;

    const nextMuted = !video.muted;
    video.muted = nextMuted;
    setIsMuted(nextMuted);

    if (nextMuted) {
      setShowMutedMessage(true);
      setTimeout(() => setShowMutedMessage(false), 1500);
    }
  };

  return (
    <div className="video" onClick={handleVideoClick}>
      <video
        className="video__player"
        ref={videoRef}
        src={url}
        playsInline
        loop
        muted={false}
        preload="auto"
      />

      {/* Sidebar —Å –∏–∫–æ–Ω–∫–∞–º–∏ */}
      <div className="video__sidebar">
        <div className="video__button">
          <img src="/icons/like.svg" alt="Like" />
          <span>{likes}</span>
        </div>
        <div className="video__button">
          <img src="/icons/comment.svg" alt="Comment" />
          <span>{messages}</span>
        </div>
        <div className="video__button">
          <img src="/icons/share.svg" alt="Share" />
          <span>{shares}</span>
        </div>
      </div>

      {/* –û–ø–∏—Å–∞–Ω–∏–µ –∏ —Å—Å—ã–ª–∫–∞ */}
      <div className="video__footer">
        <div className="video__channel">
          <strong>@{channel}</strong>
          <button>–ü–æ–¥–ø–∏—Å–∞—Ç—å—Å—è</button>
        </div>
        <div className="video__description">{description}</div>
        {isAd && adLink && (
          <a href={adLink} target="_blank" rel="noopener noreferrer" className="video__adLink">
            –ü–æ–¥—Ä–æ–±–Ω–µ–µ
          </a>
        )}
      </div>

      {/* –°–æ–æ–±—â–µ–Ω–∏–µ –æ mute */}
      {showMutedMessage && <div className="video__loading">üîá –í–∏–¥–µ–æ –±–µ–∑ –∑–≤—É–∫–∞</div>}
    </div>
  );
}

export default Video;
