import React, { useRef, useState, useEffect } from "react";
import "./Video.css";

function parseCount(count) {
  if (typeof count === "number") return count;
  if (typeof count === "string") {
    const match = count.match(/^([\d.]+)([MK]?)$/i);
    if (!match) return parseInt(count) || 0;

    const num = parseFloat(match[1]);
    const suffix = match[2].toUpperCase();

    switch (suffix) {
      case "M":
        return Math.round(num * 1_000_000);
      case "K":
        return Math.round(num * 1_000);
      default:
        return Math.round(num);
    }
  }
  return 0;
}

function formatCount(count) {
  if (count >= 1_000_000) return (count / 1_000_000).toFixed(1) + "M";
  if (count >= 1_000) return (count / 1_000).toFixed(1) + "K";
  return count.toString();
}

function Video({ url, channel, description, likes = 0, dislikes = 0, comments = 0 }) {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(true);
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);
  const [likeCount, setLikeCount] = useState(parseCount(likes));
  const [dislikeCount, setDislikeCount] = useState(parseCount(dislikes));

  // Sound toggle on click
  const toggleMute = () => {
    if (videoRef.current) {
      const muted = !videoRef.current.muted;
      videoRef.current.muted = muted;
      setIsMuted(muted);
    }
  };

  // Stop all other videos' sound
  useEffect(() => {
    const current = videoRef.current;
    const handlePlay = () => {
      document.querySelectorAll("video").forEach((vid) => {
        if (vid !== current) {
          vid.pause();
          vid.currentTime = 0;
          vid.muted = true;
        }
      });
    };

    current?.addEventListener("play", handlePlay);
    return () => {
      current?.removeEventListener("play", handlePlay);
    };
  }, []);

  const onLike = () => {
    if (isLiked) {
      setLikeCount((prev) => prev - 1);
      setIsLiked(false);
    } else {
      setLikeCount((prev) => prev + 1);
      setIsLiked(true);
      if (isDisliked) {
        setDislikeCount((prev) => prev - 1);
        setIsDisliked(false);
      }
    }
  };

  const onDislike = () => {
    if (isDisliked) {
      setDislikeCount((prev) => prev - 1);
      setIsDisliked(false);
    } else {
      setDislikeCount((prev) => prev + 1);
      setIsDisliked(true);
      if (isLiked) {
        setLikeCount((prev) => prev - 1);
        setIsLiked(false);
      }
    }
  };

  const onComment = () => {
    alert("Kommentare öffnen – in Entwicklung");
  };

  const onShare = () => {
    navigator.clipboard.writeText(window.location.href);
    alert("Link wurde kopiert!");
  };

  return (
    <div className="video">
      <video
        className="video__player"
        src={url}
        ref={videoRef}
        autoPlay
        muted
        loop
        playsInline
        onClick={toggleMute}
        controls={false}
      />

      <div className="video__sidebar">
        <div className={`video__button ${isLiked ? "active" : ""}`} onClick={onLike}>
          <img src="/icons/like.svg" alt="Like" />
          <span>{formatCount(likeCount)}</span>
        </div>
        <div className={`video__button ${isDisliked ? "active" : ""}`} onClick={onDislike}>
          <img src="/icons/dislike.svg" alt="Dislike" />
          <span>{formatCount(dislikeCount)}</span>
        </div>
        <div className="video__button" onClick={onComment}>
          <img src="/icons/comment.svg" alt="Comment" />
          <span>{formatCount(parseCount(comments))}</span>
        </div>
        <div className="video__button" onClick={onShare}>
          <img src="/icons/share.svg" alt="Share" />
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          <button>Abonnieren</button>
        </div>
        <div className="video__description">{description}</div>
      </div>
    </div>
  );
}

export default Video;
