import React, { useRef, useState, useEffect } from 'react';
import './Video.css';

function Video({ url, channel, description, likes, messages, shares, isAd, adLink, poster }) {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(true);
  const [showMutedMessage, setShowMutedMessage] = useState(false);
  const [isActive, setIsActive] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [videoSrc, setVideoSrc] = useState(null); // –î–∏–Ω–∞–º–∏—á–µ—Å–∫–∞—è —É—Å—Ç–∞–Ω–æ–≤–∫–∞ src

  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setVideoSrc(url); // –ó–∞–≥—Ä—É–∑–∫–∞ src —Ç–æ–ª—å–∫–æ –ø—Ä–∏ –≤–∏–¥–∏–º–æ—Å—Ç–∏
          setIsActive(true);
          observer.unobserve(entry.target); // –ü—Ä–µ–∫—Ä–∞—Ç–∏—Ç—å –Ω–∞–±–ª—é–¥–µ–Ω–∏–µ –ø–æ—Å–ª–µ –∑–∞–≥—Ä—É–∑–∫–∏
        }
      },
      { threshold: 0.8 }
    );

    const el = videoRef.current;
    if (el) observer.observe(el);

    return () => {
      if (el) observer.unobserve(el);
    };
  }, [url]);

  useEffect(() => {
    const video = videoRef.current;
    if (!video || !videoSrc) return;

    if (isActive) {
      video.play().catch((e) => console.warn("play error", e));
    } else {
      video.pause();
      video.currentTime = 0;
    }
  }, [isActive, videoSrc]);

  const handleClick = () => {
    setIsMuted(prev => !prev);
    setShowMutedMessage(true);
    setTimeout(() => setShowMutedMessage(false), 2000);
  };

  return (
    <div className="video" onClick={handleClick}>
      {isLoading && !videoSrc && <div className="video__loading">–ó–∞–≥—Ä—É–∑–∫–∞...</div>}
      <video
        ref={videoRef}
        className="video__player"
        src={videoSrc} // –î–∏–Ω–∞–º–∏—á–µ—Å–∫–∞—è —É—Å—Ç–∞–Ω–æ–≤–∫–∞ src
        poster={poster}
        playsInline
        loop
        muted={isMuted}
        preload="none" // –û—Ç–∫–ª—é—á–µ–Ω–∏–µ –ø—Ä–µ–¥–≤–∞—Ä–∏—Ç–µ–ª—å–Ω–æ–π –∑–∞–≥—Ä—É–∑–∫–∏
        onCanPlay={() => setIsLoading(false)}
        onWaiting={() => setIsLoading(true)}
      />
      {showMutedMessage && <div className="muted-message">–í–∏–¥–µ–æ –ø—Ä–∏–≥–ª—É—à–µ–Ω–æ</div>}
      <div className="video__info">
        <p>{channel}</p>
        <p>{description}</p>
        <div className="video__stats">
          <span>{likes} üëç</span>
          <span>{messages} üí¨</span>
          <span>{shares} üîó</span>
        </div>
        {isAd && <a href={adLink} className="video__ad">–†–µ–∫–ª–∞–º–∞</a>}
      </div>
    </div>
  );
}

export default Video;
