import React, { useState, useEffect, Suspense } from "react";
import BottomNav from "./components/BottomNav";
import "./App.css";
import videosData from "./config.json"; // Используем данные из config.json

function App() {
  const [activeId, setActiveId] = useState(0);

  // Преобразуем данные из config.json в формат, совместимый с Video
  const videos = videosData.map((video, index) => ({
    id: index + 1, // Генерируем id, так как его нет в config.json
    url: process.env.PUBLIC_URL + "/videos/" + video.src,
    channel: video.channel,
    description: video.description,
    likes: video.likes.toString() + "K", // Преобразуем в формат с "K"
    messages: video.comments.toString() + "K", // Переименуем comments в messages
    shares: video.shares ? video.shares.toString() + "K" : "0K", // Добавим shares
    isAd: video.isAd || false,
    adLink: video.link || "#",
    poster: process.env.PUBLIC_URL + "/videos/" + video.src.replace(".mp4", ".jpg"), // Пример постера
  }));

  const [visibleVideos, setVisibleVideos] = useState(videos.slice(0, 3));

  const loadMoreVideos = () => {
    const nextVideos = videos.slice(visibleVideos.length, visibleVideos.length + 3);
    setVisibleVideos((prev) => [...prev, ...nextVideos]);
  };

  useEffect(() => {
    const handleScroll = () => {
      if (window.innerHeight + window.scrollY >= document.body.offsetHeight - 500) {
        loadMoreVideos();
      }
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [visibleVideos]);

  return (
    <div className="app">
      <Suspense fallback={<div>Загрузка видео...</div>}>
        {visibleVideos.map((video) => (
          <Video
            key={video.id}
            {...video}
            isActive={activeId === video.id}
            onActivate={() => setActiveId(video.id)}
          />
        ))}
      </Suspense>
      <BottomNav />
    </div>
  );
}

export default App;
