import React, { useRef, useState, useEffect } from "react";
import "./Video.css";

function Video({
  url,
  channel,
  description,
  likes,
  messages,
  shares,
  isAd,
  adLink,
  poster,
}) {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(false); // Ð·Ð²ÑƒÐº Ð²ÐºÐ»ÑŽÑ‡ÐµÐ½ Ð¿Ð¾ ÑƒÐ¼Ð¾Ð»Ñ‡Ð°Ð½Ð¸ÑŽ
  const [showMutedMessage, setShowMutedMessage] = useState(false);
  const [isActive, setIsActive] = useState(false);
  const [isLoading, setIsLoading] = useState(true);

  // ÐÐ°Ð±Ð»ÑŽÐ´ÐµÐ½Ð¸Ðµ Ð·Ð° Ð²Ñ…Ð¾Ð¶Ð´ÐµÐ½Ð¸ÐµÐ¼ Ð² ÑÐºÑ€Ð°Ð½
  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        setIsActive(entry.isIntersecting);
      },
      { threshold: 0.8 }
    );

    const el = videoRef.current;
    if (el) observer.observe(el);

    return () => {
      if (el) observer.unobserve(el);
    };
  }, []);

  // ÐÐ²Ñ‚Ð¾Ð²Ð¾ÑÐ¿Ñ€Ð¾Ð¸Ð·Ð²ÐµÐ´ÐµÐ½Ð¸Ðµ Ð¿Ñ€Ð¸ Ð°ÐºÑ‚Ð¸Ð²Ð½Ð¾ÑÑ‚Ð¸
  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    video.muted = isMuted;

    if (isActive) {
      const playPromise = video.play();
      if (playPromise !== undefined) {
        playPromise.catch((err) => {
          console.warn("Autoplay error:", err);
        });
      }
    } else {
      video.pause();
      video.currentTime = 0;
    }
  }, [isActive, isMuted]);

  // ÐžÐ±Ñ€Ð°Ð±Ð¾Ñ‚ÐºÐ° ÐºÐ»Ð¸ÐºÐ° Ð¿Ð¾ ÑÐºÑ€Ð°Ð½Ñƒ (Ð¿ÐµÑ€ÐµÐºÐ»ÑŽÑ‡ÐµÐ½Ð¸Ðµ mute)
  const handleClick = (e) => {
    if (
      e.target.closest(".video__sidebar") ||
      e.target.closest(".video__footer")
    )
      return;

    const video = videoRef.current;
    if (!video) return;

    const nextMuted = !video.muted;
    video.muted = nextMuted;
    setIsMuted(nextMuted);

    if (nextMuted) {
      setShowMutedMessage(true);
      setTimeout(() => setShowMutedMessage(false), 1500);
    }
  };

  return (
    <div className="video" onClick={handleClick}>
      {isLoading && <div className="video__loading">Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ°...</div>}

      <video
        ref={videoRef}
        className="video__player"
        src={url}
        poster={poster}
        playsInline
        loop
        muted={isMuted}
        preload="auto"
        onCanPlay={() => setIsLoading(false)}
        onWaiting={() => setIsLoading(true)}
      />

      <div className="video__sidebar">
        <div className="video__button">
          <span className="material-symbols-outlined" style={{ fontSize: "36px" }}>thumb_up</span>
          <span>{likes}</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined" style={{ fontSize: "36px" }}>thumb_down</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined" style={{ fontSize: "36px" }}>comment</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined" style={{ fontSize: "36px" }}>share</span>
        </div>
      </div>

      <div className="video__footer">
        {!isAd ? (
          <>
            <div className="video__channel">
              <strong>@{channel}</strong>
              <button>Abonnieren</button>
            </div>
            <div className="video__description">{description}</div>
          </>
        ) : (
          <>
            <div className="video__description">{description}</div>
            <a
              href={adLink || "#"}
              className="video__register-button centered"
              target="_blank"
              rel="noopener noreferrer"
            >
              Jetzt registrieren
            </a>
          </>
        )}
      </div>

      {showMutedMessage && <div className="video__loading">ðŸ”‡ Ton ist aus</div>}
    </div>
  );
}

export default Video;
