import React, { useRef, useState, useEffect } from "react";
import "./Video.css";

function Video({
  url,
  channel,
  description,
  likes,
  comments,
  shares,
  isAd,
  adLink,
  poster,
}) {
  const videoRef = useRef(null);
  const [isActive, setIsActive] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [showMutedMessage, setShowMutedMessage] = useState(false);

  // ÐÐ²Ñ‚Ð¾Ð²Ð¾ÑÐ¿Ñ€Ð¾Ð¸Ð·Ð²ÐµÐ´ÐµÐ½Ð¸Ðµ Ð¸ Ð¿Ð°ÑƒÐ·Ð° Ð¿Ñ€Ð¸ Ð¿Ð¾Ð¿Ð°Ð´Ð°Ð½Ð¸Ð¸ Ð² Ð·Ð¾Ð½Ñƒ Ð²Ð¸Ð´Ð¸Ð¼Ð¾ÑÑ‚Ð¸
  useEffect(() => {
    const observer = new IntersectionObserver(
      ([entry]) => {
        const isVisible = entry.isIntersecting;
        setIsActive(isVisible);
        const video = videoRef.current;
        if (video) {
          if (isVisible) video.play().catch(() => {});
          else video.pause();
        }
      },
      { threshold: 0.8 }
    );

    const el = videoRef.current;
    if (el) observer.observe(el);

    return () => {
      if (el) observer.unobserve(el);
    };
  }, []);

  // ÐžÐ´Ð¸Ð½ ÐºÐ»Ð¸Ðº Ð°ÐºÑ‚Ð¸Ð²Ð¸Ñ€ÑƒÐµÑ‚ Ð·Ð²ÑƒÐº
  useEffect(() => {
    const enableSound = () => {
      const video = videoRef.current;
      if (video && video.muted) {
        video.muted = false;
        setShowMutedMessage(false);
      }
      window.removeEventListener("click", enableSound);
      window.removeEventListener("touchstart", enableSound);
    };

    window.addEventListener("click", enableSound);
    window.addEventListener("touchstart", enableSound);

    return () => {
      window.removeEventListener("click", enableSound);
      window.removeEventListener("touchstart", enableSound);
    };
  }, []);

  // ÐšÐ»Ð¸Ðº Ð¿Ð¾ Ð²Ð¸Ð´ÐµÐ¾ â€” Ð²ÐºÐ»ÑŽÑ‡ÐµÐ½Ð¸Ðµ/Ð¾Ñ‚ÐºÐ»ÑŽÑ‡ÐµÐ½Ð¸Ðµ Ð·Ð²ÑƒÐºÐ° Ð²Ñ€ÑƒÑ‡Ð½ÑƒÑŽ
  const handleVideoClick = (e) => {
    if (
      e.target.closest(".video__sidebar") ||
      e.target.closest(".video__footer")
    )
      return;

    const video = videoRef.current;
    if (video) {
      video.muted = !video.muted;
      if (video.muted) {
        setShowMutedMessage(true);
        setTimeout(() => setShowMutedMessage(false), 1500);
      }
    }
  };

  return (
    <div className="video" onClick={handleVideoClick}>
      <video
        ref={videoRef}
        className="video__player"
        src={url}
        poster={poster}
        playsInline
        loop
        muted
        preload="auto"
        onCanPlay={() => setIsLoading(false)}
        onWaiting={() => setIsLoading(true)}
      />

      {isLoading && <div className="video__loading">Ð—Ð°Ð³Ñ€ÑƒÐ·ÐºÐ°...</div>}

      <div className="video__sidebar">
        <div className="video__button">
          <span className="material-symbols-outlined">thumb_up</span>
          <span>{likes}</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined">thumb_down</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined">comment</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined">share</span>
        </div>
      </div>

      <div className="video__footer">
        {!isAd ? (
          <>
            <div className="video__channel">
              <strong>{channel}</strong>
              <button>Abonnieren</button>
            </div>
            <div className="video__description">{description}</div>
          </>
        ) : (
          <>
            <div className="video__description">{description}</div>
            <a
              href={adLink || "#"}
              target="_blank"
              rel="noopener noreferrer"
              className="video__register-button centered"
            >
              Jetzt registrieren
            </a>
          </>
        )}
      </div>

      {showMutedMessage && (
        <div className="video__muted-notice">ðŸ”‡ Ton ist aus</div>
      )}
    </div>
  );
}

export default Video;
