import React, { useRef, useState, useEffect, useCallback } from 'react';
import './Video.css';

function Video({ url, channel, description, likes, messages, shares, isAd, adLink, poster }) {
  const videoRef = useRef(null);
  const [isMuted, setIsMuted] = useState(true);
  const [showMutedMessage, setShowMutedMessage] = useState(false);
  const [isActive, setIsActive] = useState(false);
  const [isLoading, setIsLoading] = useState(true);
  const [videoSrc, setVideoSrc] = useState(null);

  // –≠–ª–µ–≥–∞–Ω—Ç–Ω–∞—è –æ–±—Ä–∞–±–æ—Ç–∫–∞ –≤–∏–¥–∏–º–æ—Å—Ç–∏ –≤–∏–¥–µ–æ
  const handleIntersection = useCallback((entries) => {
    const [entry] = entries;
    if (entry.isIntersecting) {
      setVideoSrc(url);
      setIsActive(true);
    }
  }, [url]);

  useEffect(() => {
    const observer = new IntersectionObserver(handleIntersection, { threshold: 0.8 });
    const el = videoRef.current;
    if (el) observer.observe(el);

    return () => observer.disconnect(); // –¶–∞—Ä—Å–∫–∞—è –æ—á–∏—Å—Ç–∫–∞
  }, [handleIntersection]);

  useEffect(() => {
    const video = videoRef.current;
    if (!video || !videoSrc) return;

    const playVideo = async () => {
      try {
        if (isActive) await video.play();
        else {
          video.pause();
          video.currentTime = 0;
        }
      } catch (e) {
        console.warn('–û—à–∏–±–∫–∞ –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏—è:', e);
      }
    };
    playVideo();
  }, [isActive, videoSrc]);

  const handleClick = useCallback(() => {
    setIsMuted(prev => !prev);
    setShowMutedMessage(true);
    setTimeout(() => setShowMutedMessage(false), 2000);
  }, []);

  return (
    <div className="video" onClick={handleClick}>
      {isLoading && !videoSrc && (
        <div className="video__loading">–ó–∞–≥—Ä—É–∑–∫–∞ –≤–∏–¥–µ–æ...</div>
      )}
      <video
        ref={videoRef}
        className="video__player"
        src={videoSrc}
        poster={poster}
        playsInline
        loop
        muted={isMuted}
        preload="none"
        onCanPlay={() => setIsLoading(false)}
        onWaiting={() => setIsLoading(true)}
      />
      {showMutedMessage && (
        <div className="muted-message">–í–∏–¥–µ–æ –ø—Ä–∏–≥–ª—É—à–µ–Ω–æ ‚ú®</div>
      )}
      <div className="video__info">
        <h3 className="video__channel">{channel}</h3>
        <p className="video__description">{description}</p>
        <div className="video__stats">
          <span>{likes} üëç</span>
          <span>{messages} üí¨</span>
          <span>{shares} üîó</span>
        </div>
        {isAd && <a href={adLink} className="video__ad">–†–µ–∫–ª–∞–º–∞ üé©</a>}
      </div>
    </div>
  );
}

export default Video;
