import React, { useRef, useEffect, useState } from "react";
import "./Video.css";

function Video({
  url,
  channel,
  description,
  likes,
  comments,
  shares,
  isAd,
  adLink,
}) {
  const videoRef = useRef(null);
  const [mutedFallback, setMutedFallback] = useState(false);

  useEffect(() => {
    const video = videoRef.current;
    if (!video) return;

    video.muted = false; // –æ—Ç–∫–ª—é—á–∞–µ–º mute –ø–æ —É–º–æ–ª—á–∞–Ω–∏—é

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          playWithSound(video);
        } else {
          video.pause();
        }
      },
      { threshold: 0.75 }
    );

    observer.observe(video);

    return () => observer.disconnect();
  }, []);

  const playWithSound = (video) => {
    // –ø—ã—Ç–∞–µ–º—Å—è –≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ—Å—Ç–∏ —Å –≤–∫–ª—é—á—ë–Ω–Ω—ã–º –∑–≤—É–∫–æ–º
    video.muted = false;
    const promise = video.play();

    if (promise !== undefined) {
      promise
        .then(() => {
          setMutedFallback(false);
        })
        .catch((err) => {
          // –ë—Ä–∞—É–∑–µ—Ä –Ω–µ —Ä–∞–∑—Ä–µ—à–∞–µ—Ç –∞–≤—Ç–æ–∑–≤—É–∫ ‚Äî fallback –Ω–∞ mute
          console.warn("–ê–≤—Ç–æ–≤–æ—Å–ø—Ä–æ–∏–∑–≤–µ–¥–µ–Ω–∏–µ —Å–æ –∑–≤—É–∫–æ–º –∑–∞–ø—Ä–µ—â–µ–Ω–æ:", err);
          video.muted = true;
          video.play();
          setMutedFallback(true);
        });
    }
  };

  return (
    <div className="video">
      <video
        ref={videoRef}
        className="video__player"
        src={url}
        loop
        playsInline
        preload="auto"
      />

      {mutedFallback && (
        <div className="video__muted-notice">
          üîá –ó–≤—É–∫ –æ—Ç–∫–ª—é—á—ë–Ω –±—Ä–∞—É–∑–µ—Ä–æ–º
        </div>
      )}

      <div className="video__sidebar">
        <div className="video__button">
          <span className="material-symbols-outlined">thumb_up</span>
          <span>{likes}</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined">thumb_down</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined">comment</span>
        </div>
        <div className="video__button">
          <span className="material-symbols-outlined">share</span>
        </div>
      </div>

      <div className="video__footer">
        {!isAd ? (
          <>
            <div className="video__channel">
              <strong>{channel}</strong>
              <button>Abonnieren</button>
            </div>
            <div className="video__description">{description}</div>
          </>
        ) : (
          <>
            <div className="video__description">{description}</div>
            <a
              href={adLink || "#"}
              target="_blank"
              rel="noopener noreferrer"
              className="video__register-button centered"
            >
              Jetzt registrieren
            </a>
          </>
        )}
      </div>
    </div>
  );
}

export default Video;
