import React, { useState } from "react";
import "./Videos.css";

function formatNumber(n) {
  if (n >= 1_000_000) return (n / 1_000_000).toFixed(1) + "M";
  if (n >= 1_000) return (n / 1_000).toFixed(1) + "K";
  return n.toString();
}

function Video({ url, channel, description, likes, dislikes, comments }) {
  // ВАЖНО: сохраняем только числа
  const [likeCount, setLikeCount] = useState(Number(likes));
  const [dislikeCount, setDislikeCount] = useState(Number(dislikes));
  const [isLiked, setIsLiked] = useState(false);
  const [isDisliked, setIsDisliked] = useState(false);

  const toggleLike = () => {
    if (isLiked) {
      setLikeCount(likeCount - 1);
      setIsLiked(false);
    } else {
      setLikeCount(likeCount + 1);
      if (isDisliked) {
        setDislikeCount(dislikeCount - 1);
        setIsDisliked(false);
      }
      setIsLiked(true);
    }
  };

  const toggleDislike = () => {
    if (isDisliked) {
      setDislikeCount(dislikeCount - 1);
      setIsDisliked(false);
    } else {
      setDislikeCount(dislikeCount + 1);
      if (isLiked) {
        setLikeCount(likeCount - 1);
        setIsLiked(false);
      }
      setIsDisliked(true);
    }
  };

  return (
    <div className="video">
      <video
        className="video__player"
        src={url}
        autoPlay
        loop
        muted
        playsInline
      ></video>

      <div className="video__sidebar">
        <div className="video__button" onClick={toggleLike}>
          <img src="/icons/like.svg" alt="Like" />
          <span>{formatNumber(likeCount)}</span>
        </div>
        <div className="video__button" onClick={toggleDislike}>
          <img src="/icons/dislike.svg" alt="Dislike" />
          <span>{formatNumber(dislikeCount)}</span>
        </div>
        <div className="video__button">
          <img src="/icons/comment.svg" alt="Comment" />
          <span>{formatNumber(Number(comments))}</span>
        </div>
        <div className="video__button">
          <img src="/icons/share.svg" alt="Share" />
        </div>
      </div>

      <div className="video__footer">
        <div className="video__channel">
          <p>@{channel}</p>
          <button className="subscribe-button">Подписаться</button>
        </div>
        <div className="video__description">{description}</div>
      </div>
    </div>
  );
}

export default Video;
